// Helper function to get options by category
export const getOptionsByCategory = (categoryId: string): EditingOption[] => {
  return EDITING_OPTIONS.filter(option => option.category === categoryId);
};

// Helper function to get all basic (non-advanced) options
export const getBasicOptions = (): EditingOption[] => {
  return EDITING_OPTIONS.filter(option => !option.advanced);
};

// Helper function to get all advanced options
export const getAdvancedOptions = (): EditingOption[] => {
  return EDITING_OPTIONS.filter(option => option.advanced);
};

// Get default values for all options
export const getDefaultSettings = (): Record<string, number | boolean | string> => {
  const defaults: Record<string, number | boolean | string> = {};
  EDITING_OPTIONS.forEach(option => {
    defaults[option.id] = option.default;
  });
  return defaults;
};

